import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Car } from 'src/app/models/car.model';
import { CarService } from 'src/app/services/car.service';

@Component({
  selector: 'app-card-car',
  templateUrl: './card-car.component.html',
  styleUrls: ['./card-car.component.scss']

})
export class CardCarComponent implements OnInit {

  carros$= new Observable<Car[]>();

  constructor(private carService: CarService) { }

  ngOnInit(): void {
    this.carService.obterCarros();
  }

  obterCarros() {
    this.carros$ = this.carService.obterCarros();
  }

}
<div class="card " style="width: 18rem;" >
  <img src="./assets/vitrine-car.jpg" class="card-img-top" alt="Imagem de um carro">
  <div class="card-body">

    <h5 class="card-title p-2 g-col-6"> Onix</h5>
    <p class="card-text p-2 g-col-6"> Chevrolet</p>
    <p class="card-text p-2 g-col-6"> 2017</p>
    <p class="card-text p-2 g-col-6"> 38.990,00</p>
  </div>
</div>

