import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'order'
})
export class OrderPipe implements PipeTransform {

  transform(list: Object[], ...params: string[]): Object[] {

    if (params[0] === 'menor') {
      return list.sort((a, b) => a[params[1]] - b[params[1]]);
    } else if (params[0] === 'maior') {
      return list.sort((a, b) => b[params[1]] - a[params[1]]);
    }

    return null;
  }

}
