import { Component } from '@angular/core';
import { CarService } from 'src/app/services/car.service';
import { Car } from 'src/app/models/car.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent {

  carro$: any[] = [];

  constructor(private carService: CarService) { }

  ngOnInit(): void {
    this.carService.obterCarros().subscribe((carros) => {
      this.carro$ = carros;

      console.log(this.carro$);
    });
  }

  deletarCarro(id: number) {
    this.carService.deletarCarro(id).subscribe(() => {
      this.carro$ = this.carro$.filter(carro => carro.id !== id);
    });
  }

  cadastrarCarro(car: Car) {
    this.carService.cadastrarCarro(car).subscribe((carro) => {
      this.carro$.push(carro);
    });
  }

  atualizarCarro(car: Car) {
    this.carService.atualizarCarro(car).subscribe(() => {
      this.carro$ = this.carro$.map((carro) => {
        if (carro.id === car.id) {
          carro = car;
        }
        return carro;
      });
    });
  }



}
