import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Car } from 'src/app/models/car.model';
import { CarService } from 'src/app/services/car.service';

@Component({
  selector: 'app-card-car',
  templateUrl: './card-car.component.html',
  styleUrls: ['./card-car.component.scss']

})
export class CardCarComponent implements OnInit {

  carros$= new Observable<Car[]>();

  carro$: any[] = [];


  constructor(private carService: CarService) { }

  ngOnInit(): void {
    this.carService.obterCarros();
    this.carService.obterCarros().subscribe((carros) => {
      this.carro$ = carros;
    });
  }

  obterCarros() {
    this.carros$ = this.carService.obterCarros();
  }

}
<div class="card" style="width: 18rem;" >
  <img src="./assets/vitrine-car.jpg" class="card-img-top" alt="Imagem de um carro">
  <div class="card-body row text-center">
    <div class="col-6 ">
      <p class="card-text ajuste "> Onix</p>
      <p class="card-text"> 2017</p>
    </div>
    <div class="col-6">
      <p class="card-text"> Chevrolet</p>
      <p class="card-text"> 38.990,00</p>
    </div>
  </div>
</div>

<div class="card row row-cols-1 row-cols-md-3 g-4 mt-5">
  <div class="col" *ngFor="let carro of carro$">
    <div class="card h-100">
      <img [src]="carro.imagem" class="card-img-top" alt="{{carro.d_alt}}">
      <div class="card-body">
        <h5 class="card-title
          text-truncate">{{carro.nome}} {{carro.marca}}</h5>
        <p class="card-text">{{carro.modelo}}</p>
        <p class="card-text">{{carro.preco}}</p>
      </div>
    </div>
  </div>
</div>
