import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Car } from 'src/app/models/car.model';
import { CarService } from 'src/app/services/car.service';

@Component({
  selector: 'app-card-car',
  templateUrl: './card-car.component.html',
  styleUrls: ['./card-car.component.scss'],
  standalone: true,
  imports: [CommonModule],
})
export class CardCarComponent implements OnInit {
  carServiceList: any;

  carros$= new Observable<Car[]>();

  constructor(private carService: CarService) { }

  ngOnInit(): void {
    this.carServiceList = this.carService.obterCarros();
  }


}
<div class="card" style="width: 18rem;" *ngFor="let carro of carros">
  <img src="{{imagem}}" class="card-img-top" alt="{{d_alt}}">
  <div class="card-body">
    <h5 class="card-title">{{nome}} Onix</h5>
    <p class="card-text">{{marca}} Chevrolet</p>
    <p class="card-text">{{modelo}} 2017</p>
    <p class="card-text">{{preco}} 38.990,00</p>
  </div>
</div>
