import { Component, OnInit } from '@angular/core';
import { Car } from 'src/app/models/car.model';
import { environment } from 'src/environments/environment';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})
export class SearchComponent implements OnInit {

  constructor() {
    console.log('to aqui', environment.api);
   }

  ngOnInit(): void {
  }

}
<div class="container">
  <div class="d-flex justify-content-end">
    <button type="button" class="btn btn-light mt-5 mx-2" data-order="asc">
      <span class="me-1">Maior Valor</span>
      <span class="fas fa-sort-up"></span> <!-- Ícone de seta para cima -->
    </button>
    <button type="button" class="btn btn-light mt-5" data-order="desc">
      <span class="me-1">Menor Valor</span>
      <span class="fas fa-sort-down"></span> <!-- Ícone de seta para baixo -->
    </button>
  </div>
  <div class="card" style="width: 18rem;" *ngFor="let carro of carros">
    <img src="{{imagem}}" class="card-img-top" alt="{{d_alt}}">
    <div class="card-body">
      <h5 class="card-title">{{nome}} Onix</h5>
      <p class="card-text">{{marca}} Chevrolet</p>
      <p class="card-text">{{modelo}} 2017</p>
      <p class="card-text">{{preco}} 38.990,00</p>
    </div>
  </div>
</div>
