<div class="row row-cols-1 row-cols-md-3 g-4 mt-5">
  <div class="col" *ngFor="let carro of carros | async">
    <div class="card h-100">
      <img [src]="carro.imagem" class="card-img-top" alt="{{carro.d_alt}}">
      <div class="card-body">
        <h5 class="card-title
          text-truncate">{{carro.nome}} {{carro.marca}}</h5>
        <p class="card-text">{{carro.modelo}}</p>
        <p class="card-text">{{carro.preco}}</p>
      </div>
    </div>
  </div>
</div>

<div class="d-flex justify-content-end">
  <button type="button" class="btn btn-light mt-5 mx-2" data-order="asc">
    <span class="me-1">Maior Valor</span>
    <span class="fas fa-sort-up"></span>
  </button>
  <button type="button" class="btn btn-light mt-5" data-order="desc">
    <span class="me-1">Menor Valor</span>
    <span class="fas fa-sort-down"></span>
  </button>
</div>
